interface DataTableSliderProps<TData> {
  column: Column<TData, unknown>;
  title?: string;
}

export function DataTableSlider<TData>({
  column,
  title,
}: DataTableSliderProps<TData>) {
  const id = React.useId();
  const columnFilterValue = column.getFilterValue() as
    | [number, number]
    | undefined;

  const unit = column.columnDef.meta?.unit;

  const [min, max] = React.useMemo(() => {
    const range = column.columnDef.meta?.range;
    if (range) return range;

    const values = column.getFacetedMinMaxValues();
    if (!values) return [0, 100];

    return [values[0], values[1]];
  }, [column]);

  const [range, setRange] = React.useState<[number, number]>(
    columnFilterValue ?? [min, max],
  );

  const onRangeChange = React.useCallback(
    (values: [number, number]) => {
      setRange(values);
      column.setFilterValue(values);
    },
    [column],
  );

  const onReset = React.useCallback(
    (event?: React.MouseEvent) => {
      event?.stopPropagation();
      setRange([min, max]);
      column.setFilterValue(undefined);
    },
    [column, min, max],
  );

  const formatValue = React.useCallback((value: number) => {
    return value.toLocaleString(undefined, {
      maximumFractionDigits: 0,
    });
  }, []);

  return (
    <Popover>
      <PopoverTrigger asChild>
        <Button variant="outline" size="sm" className="border-dashed">
          {columnFilterValue ? (
            <div
              aria-label="Clear filter"
              role="button"
              tabIndex={0}
              onClick={onReset}
              className="rounded-sm opacity-70 transition-opacity hover:opacity-100 focus-visible:outline-none focus-visible:ring-1 focus-visible:ring-ring"
            >
              <XCircle />
            </div>
          ) : (
            <PlusCircle />
          )}
          <span>{title}</span>
          {columnFilterValue ? (
            <>
              <Separator orientation="vertical" className="mx-0.5 h-4" />
              {formatValue(columnFilterValue[0])} -{" "}
              {formatValue(columnFilterValue[1])}
              {unit ? ` ${unit}` : ""}
            </>
          ) : null}
        </Button>
      </PopoverTrigger>
      <PopoverContent align="start" className="flex w-auto flex-col gap-4">
        <div className="flex flex-col gap-3">
          <p className="font-medium leading-none peer-disabled:cursor-not-allowed peer-disabled:opacity-70">
            {title}
          </p>
          <div className="flex items-center gap-4">
            <Label htmlFor={`${id}-from`} className="sr-only">
              From
            </Label>
            <div className="relative">
              <Input
                id={`${id}-from`}
                type="text"
                aria-valuemin={min}
                aria-valuemax={max}
                inputMode="numeric"
                pattern="[0-9]*"
                placeholder={min.toString()}
                min={min}
                max={max}
                value={range[0].toString()}
                onChange={(event) => {
                  const numValue = Number(event.target.value);
                  if (
                    !Number.isNaN(numValue) &&
                    numValue >= min &&
                    numValue <= range[1]
                  ) {
                    onRangeChange([numValue, range[1]]);
                  }
                }}
                className="h-8 w-24 pr-8"
              />
              {unit && (
                <span className="absolute top-0 right-0 bottom-0 flex items-center rounded-r-md bg-accent px-2 text-muted-foreground text-sm">
                  {unit}
                </span>
              )}
            </div>
            <Label htmlFor={`${id}-to`} className="sr-only">
              to
            </Label>
            <div className="relative">
              <Input
                id={`${id}-to`}
                type="text"
                aria-valuemin={min}
                aria-valuemax={max}
                inputMode="numeric"
                pattern="[0-9]*"
                placeholder={max.toString()}
                min={min}
                max={max}
                value={range[1].toString()}
                onChange={(event) => {
                  const numValue = Number(event.target.value);
                  if (
                    !Number.isNaN(numValue) &&
                    numValue <= max &&
                    numValue >= range[0]
                  ) {
                    onRangeChange([range[0], numValue]);
                  }
                }}
                className="h-8 w-24 pr-8"
              />
              {unit && (
                <span className="absolute top-0 right-0 bottom-0 flex items-center rounded-r-md bg-accent px-2 text-muted-foreground text-sm">
                  {unit}
                </span>
              )}
            </div>
          </div>
          <Slider
            aria-label="Select range"
            min={min}
            max={max}
            value={range}
            onValueChange={(value) => onRangeChange(value as [number, number])}
            className="[&_[role=slider]]:h-4 [&_[role=slider]]:w-4"
          />
        </div>
        <Button
          aria-label="Reset range filter"
          variant="outline"
          size="sm"
          onClick={onReset}
          className="px-3 text-xs"
        >
          Reset
        </Button>
      </PopoverContent>
    </Popover>
  );
}

